 adj\_matrix[j, i] = 1 - p\_win

 return adj\_matrix

 def calculate\_win\_probability(self, team\_i: Team, team\_j: Team):
 seed\_diff = team\_j.team\_seed - team\_i.team\_seed
 ken\_pom\_diff = team\_i.ken\_pom\_score - team\_j.ken\_pom\_score
 return expit(self.k \* (ken\_pom\_diff + seed\_diff))

 def play\_rounds(self):
 remaining\_teams = list(range(len(self.teams)))
 round\_num = 0

 while len(remaining\_teams) > 1:
 winners = []
 for i in range(0, len(remaining\_teams), 2):
 team\_i = remaining\_teams[i]
 team\_j = remaining\_teams[i + 1]
 p\_win\_i = self.adj\_matrix[team\_i, team\_j]
 win\_i = np.random.rand() < p\_win\_i
 winning\_team\_index = i if win\_i else i + 1
 winners.append(winning\_team\_index)

 winning\_team\_name = self.teams[winning\_team\_index]
 self.round\_win\_counts[round\_num][winning\_team\_name] += 1

 self.round\_winners[round\_num] = [self.teams[i] for i in winners] # Update round\_winners dictionary
 remaining\_teams = winners
 round\_num += 1

 def play\_single\_round(self, remaining\_teams):
 winners = []
 for i in range(0, len(remaining\_teams), 2):
 team\_i = remaining\_teams[i]
 team\_j = remaining\_teams[i + 1]

 p\_win\_i = self.adj\_matrix[team\_i, team\_j]
 win\_i = np.random.rand() < p\_win\_i
 winning\_team\_index = team\_i if win\_i else team\_j
 winners.append(winning\_team\_index)

 return winners

 def get\_remaining\_teams(self, round\_num):
 if round\_num == 0:
 return list(range(len(self.teams)))

 remaining\_teams = []
 for i, team in enumerate(self.teams):
 if self.round\_winners[round\_num - 1].count(team) > 0:
 remaining\_teams.append(i)

 return remaining\_teams

 def simulate\_round\_n(self, round\_num, num\_simulations):
 round\_win\_counts = Counter()

 for \_ in range(num\_simulations):
 remaining\_teams = self.get\_remaining\_teams(round\_num)
 winners = self.play\_single\_round(remaining\_teams)
 for winner in winners:
 round\_win\_counts[self.teams[winner]] += 1

 # Update the round\_winners and round\_win\_counts
 sorted\_teams = sorted(round\_win\_counts.items(), key=lambda x: x[1], reverse=True)
 self.round\_winners[round\_num] = [team for i, (team, count) in enumerate(sorted\_teams) if i % 2 == 0]
 self.round\_win\_counts[round\_num] = round\_win\_counts

 return round\_win\_counts

 def run\_all\_rounds(self, num\_simulations):
 num\_teams = len(self.teams)
 num\_rounds = int(math.log2(num\_teams))

 for round\_num in range(num\_rounds - 1): # There are 6 rounds in a 64-team tournament
 round\_win\_counts = self.simulate\_round\_n(round\_num, num\_simulations)
 print(f"Round {round\_num + 1} results:")
 for i, (team, count) in enumerate(
 round\_win\_counts.most\_common(len(self.get\_remaining\_teams(round\_num)) // 2)):
 opponent = self.get\_opponent(round\_num, team)
 win\_percentage = (count / num\_simulations) \* 100
 print(f" {i + 1}. {team} over {opponent} with {win\_percentage:.2f}%")
 print()

 def get\_team\_index\_by\_name(self, team\_name):
 try:
 return self.teams.index(team\_name)
 except ValueError:
 raise Exception(f"Team '{team\_name}' not found in the teams list.")

 def calculate\_round\_win\_averages(self, num\_simulations):
 round\_win\_averages = [{} for \_ in range(len(self.round\_win\_counts))]
 for i, round\_win\_count in enumerate(self.round\_win\_counts):
 for team, count in round\_win\_count.items():
 round\_win\_averages[i][team] = count / num\_simulations
 return round\_win\_averages

 @staticmethod
 def error\_function(k, average\_kenpom\_difference):
 error = 0
 for matchup, historical\_probability in HISTORICAL\_SEED\_WIN\_RATES.items():
 difference = average\_kenpom\_difference[matchup]
 probability = 1 / (1 + exp(-k \* difference))
 error += (probability - historical\_probability) \*\* 2
 return error

 @staticmethod
 def average\_kenpom\_difference(max\_seed=16, kenpom\_range=(0, 40)):
 min\_kenpom, max\_kenpom = kenpom\_range
 kenpom\_increment = (max\_kenpom - min\_kenpom) / max\_seed
 average\_difference = {}

 for higher\_seed in range(1, max\_seed + 1):
 for lower\_seed in range(higher\_seed + 1, max\_seed + 1):
 higher\_seed\_kenpom = max\_kenpom - (higher\_seed - 1) \* kenpom\_increment
 lower\_seed\_kenpom = max\_kenpom - (lower\_seed - 1) \* kenpom\_increment
 average\_difference[(higher\_seed, lower\_seed)] = higher\_seed\_kenpom - lower\_seed\_kenpom

 return average\_difference

