  // Define a task that each worker will run to generate a screenshot.
  cluster.task(async ({ page, data: job }) => {
     // Set the viewport and user agent (just in case).
     await page.setViewport({
        width: 1280,
        height: 800
     });

     // Navigate to the URL and wait for it to load.
     await page.goto(job.url, {
        waitUntil: 'networkidle2',
     });

     // Take a screenshot of the page and save it to a file.
     const screenshot = await page.screenshot();
     await writeFile(job.screenshotPath, screenshot);
  });

  // Define a function that schedules a job to generate a screenshot for a given URL.
  const scheduleScreenshotJob = async (job) => {
     // Create a filename for the screenshot using the current date and time.
     const timestamp = new Date().toISOString();
     const screenshotPath = `screenshots/${timestamp}.png`;

     // Add the screenshot path to the job object.
     job.screenshotPath = screenshotPath;

     // Schedule a job to generate the screenshot.
     scheduleJob(job.schedule, async () => {
        // Add the job to the queue to be processed by the cluster.
        cluster.queue(job);
     });
  };

  // Schedule jobs for each URL in the array.
  for (const job of jobs) {
     await scheduleScreenshotJob(job);
  }

  // Wait for the cluster to finish processing all jobs.
  await cluster.idle();

  // Close the cluster.
  await cluster.close();
